import React, { useState } from "react";
import "./index.scss"
import Thumbnail from "../../Shared/UIElements/Thumbnail/index"
import ThumbnailData from "../../data"
import Modal from '../../Shared/Components/SeriesModal/index';

const VideoOnDemand = () =>{

  const [showModal, setShowModal] = useState(false);
  const [selectedThumbnail, setSelectedThumbnail] = useState(null);

  const handleThumbnailClick = (index) => {
    setSelectedThumbnail(index);
    setShowModal(true);
  };


  const handleCloseModal = () => {
    setShowModal(false);
  };

    return (
        <body className="innerPagesLayout">
            <h2 className="mb-4 mt-5 fav-heading">Video On Demand</h2>
        <div className="thumbnail-wraper">

        {ThumbnailData.map((item, index) => (
          <div key={index} className="thumbnail" onClick={() => handleThumbnailClick(index)}>
          <Thumbnail
            key={index}
            imageSrc={item.imageSrc}
            title={item.title}
            paragraph={item.paragraph}
          />
          </div>

        ))}


    </div>
    <Modal isOpen={showModal} onClose={handleCloseModal}>
        {selectedThumbnail !== null && (
          <div>
            <h2>{ThumbnailData[selectedThumbnail].title}</h2>
            {ThumbnailData[selectedThumbnail].imageSrc}
            <p>{ThumbnailData[selectedThumbnail].paragraph}</p>
          </div>
        )}
      </Modal>

        </body>
    );
}

export default VideoOnDemand;
