import React, { useState } from 'react';
import Nav from 'react-bootstrap/Nav';
import './NavLinks.css';
import ButtonBox from '../UIElements/Button';
import { Link } from 'react-router-dom/cjs/react-router-dom.min';
import SearchBar from './Search';
import { ReactComponent as ProfileIcon } from '../../Assets/Images/profile.svg';


const NavLinks = props => {
  const [activeLink, setActiveLink] = useState(window.location.pathname);

  useEffect(() => {
    // Update activeLink when the route changes
    const handleRouteChange = () => {
      setActiveLink(window.location.pathname);
    };
    window.addEventListener('popstate', handleRouteChange);
    return () => window.removeEventListener('popstate', handleRouteChange);
  }, []);

  return (
    <React.Fragment>
      <Nav className="me-auto">
        {/* ... loggedIn conditional logic ... */}

        {/* Employ Link components for active link styling */}
        <Link to="/dashboard" className={activeLink === '/' ? 'active' : ''}>
          Home
        </Link>
        <Link
          to="/video-on-demand"
          className={activeLink === '/video-on-demand' ? 'active' : ''}
        >
          Video on Demand
        </Link>
        {/* ... other links ... */}
      </Nav>
      {/* ... other Nav JSX elements ... */}
    </React.Fragment>
  );
};

export default NavLinks;
