import React, { useState } from 'react';
import Nav from 'react-bootstrap/Nav';
import './NavLinks.css';
import ButtonBox from '../UIElements/Button';
import { Link } from 'react-router-dom';
import SearchBar from './Search';
import { ReactComponent as ProfileIcon } from '../../Assets/Images/profile.svg';

const NavLinks = (props) => {
  const [activeLink, setActiveLink] = useState('');

  const handleLinkClick = (link) => {
    setActiveLink(link);
  };

  const logedIn = true; // Assuming this is your login state

  return (
    <React.Fragment>
      <Nav className="me-auto">
        {logedIn ? (
          <React.Fragment>
            <Nav.Link
              href="/dashboard"
              onClick={() => handleLinkClick('/dashboard')}
              className={activeLink === '/dashboard' ? 'active' : ''}
            >
              Home
            </Nav.Link>
            <Nav.Link
              href="/video-on-demand"
              onClick={() => handleLinkClick('/video-on-demand')}
              className={activeLink === '/video-on-demand' ? 'active' : ''}
            >
              Video on Demand
            </Nav.Link>
            <Nav.Link
              href="/live"
              onClick={() => handleLinkClick('/live')}
              className={activeLink === '/live' ? 'active' : ''}
            >
              Live Videos
            </Nav.Link>
            <Nav.Link
              href="/favorites"
              onClick={() => handleLinkClick('/favorites')}
              className={activeLink === '/favorites' ? 'active' : ''}
            >
              Favorites
            </Nav.Link>
          </React.Fragment>
        ) : (
          <div></div>
        )}
      </Nav>

      <Nav>
        {logedIn ? (
          <div className="searchProfileWrap">
            <div>
              <SearchBar />
            </div>
            <div>
              <Link to="/profile">
                <ProfileIcon />
              </Link>
            </div>
          </div>
        ) : (
          <Link to="/login">
            <ButtonBox className="primary-button" text="Sign In" />
          </Link>
        )}
      </Nav>
    </React.Fragment>
  );
};

export default NavLinks;
